import React, { useState } from "react";
import Select from "./Select";
import Input from "./Input";
import { units } from "../units";
const factor  = [
  {
    label: "Meter",
    value: "Meter",
  },
  {
    label: "Centimeter",
    value: "Centimeter",
  },
  {
    label: "Millimeter",
    value: "Millimeter",
  },
  {
    label: "Kilometer",
    value: "Kilometer",
  },
];


function App() {
  const [result, setResult] = useState(0);

  function handleClick() {
    setResult(this.refs.From.value);

  }
  return (
    <>
      <div className="converter-form">
        <label>
    Amount:
    <input type="text" name="Amount" />
  </label>
        <div className="row">
          <div>
          <label>
From:
        <select onChange={(e) => this.setState({ To: e.target.From })}>
            {factor.map((option) => (
              <option value={option.value}>{option.label}</option>
            ))}
          </select>
  </label>
          </div>
          <div>

          <label>
To:
        <select  onChange={(e) => this.setState({ To: e.target.To })}>
            {factor.map((option) => (
              <option value={option.value}>{option.label}</option>
            ))}
          </select>
  </label>
          </div>

          <button  onClick={handleClick}>Convert</button>
        </div>
      </div>

      <div id="result">
        Result is: <span data-testid="result">{result}</span>
      </div>
    </>
  );
}

export default App;
